mapper.select:
select no, title, userid, writeDate, hit, pin
from(
	select rownum rnum, no, title, userid, writeDate, hit, pin
	from(
				select no, title, userid, writeDate, hit, pin
				from notice
				order by pin desc, no asc
	)
)

select no, title, content, userid, writeDate, hit
from notice
where no=#{no}

mapper.update:
update notice
set hit = hit + 1
where no=#{no}
